mapper.update:
UPDATE member
SET m_pw=#{m_pw}, m_name=#{m_name}, m_level=#{m_level}, m_mail=#{m_mail}, 
	m_black=#{m_black}, m_phone=#{m_phone}
WHERE m_code=#{m_code}

mapper.select:
SELECT *
FROM member
WHERE m_id=#{m_id} and m_pw=#{m_pw}

SELECT *
FROM member
WHERE m_name = #{m_name} AND m_mail = #{m_mail}

mapper.update:
UPDATE member
SET m_pw = #{m_pw}
WHERE m_id = #{m_id} AND m_mail = #{m_mail}
